    "language": "en_US",
    "components": [
        {
            "type": "HEADER",
            "format": "TEXT",
            "text": "Our new sale starts {{1}}!",
            "example": {
                "header_text": [
                    "December 1st"
                ]
            }
        },
        {
            "type": "BODY",
            "text": "Get up to {{1}}% off on all items. Valid until {{2}}.",
            "example": {
                "body_text": [
                    [
                        "50",
                        "December 31st"
                    ]
                ]
            }
        },
